(170, 'attitude_theme_options', 'a:43:{s:22:"hide_header_searchform";s:1:"0";s:14:"disable_slogan";s:1:"0";s:12:"home_slogan1";s:130:"Pleasure Solutions is a rapidly growing CMS (WordPress, Joomla, Drupal), web solutions and custom application development company.";s:12:"home_slogan2";s:0:"";s:15:"slogan_position";s:12:"below-slider";s:14:"disable_slider";s:1:"1";s:19:"exclude_slider_post";s:1:"0";s:14:"default_layout";s:13:"right-sidebar";s:12:"reset_layout";s:1:"0";s:10:"custom_css";s:0:"";s:15:"disable_favicon";s:1:"0";s:7:"favicon";s:62:"http://pleasuresolutions.com/wp-content/uploads/2013/06/ps.png";s:19:"disable_webpageicon";s:1:"1";s:11:"webpageicon";s:0:"";s:15:"slider_quantity";s:1:"4";s:20:"featured_post_slider";a:2:{i:1;i:1;i:2;i:33;}s:17:"transition_effect";s:4:"fade";s:16:"transition_delay";s:1:"4";s:19:"transition_duration";s:1:"1";s:15:"social_facebook";s:65:"https://www.facebook.com/pages/Pleasure-Solutions/476508165765003";s:14:"social_twitter";s:37:"https://www.twitter.com/pleasure_soln";s:17:"social_googleplus";s:0:"";s:16:"social_pinterest";s:0:"";s:12:"social_vimeo";s:0:"";s:15:"social_linkedin";s:0:"";s:13:"social_flickr";s:0:"";s:13:"social_tumblr";s:0:"";s:14:"social_myspace";s:0:"";s:10:"social_rss";s:0:"";s:14:"social_youtube";s:0:"";s:19:"google_verification";s:13:"UA-35757475-1";s:18:"yahoo_verification";s:0:"";s:17:"bing_verification";s:0:"";s:15:"analytic_header";s:0:"";s:15:"analytic_footer";s:0:"";s:8:"feed_url";s:0:"";s:19:"front_page_category";a:1:{i:0;s:1:"0";}s:11:"header_logo";s:72:"http://pleasuresolutions.com/wp-content/uploads/2013/06/logo1-300x73.png";s:11:"header_show";s:11:"header-logo";s:11:"button_text";s:7:"Company";s:20:"redirect_button_link";s:41:"http://www.pleasuresolutions.com/company/";s:11:"site_layout";s:13:"narrow-layout";i:0;b:0;}', 'yes'),
